insert into tds.activity(activity_name,activity_group) select 'PAN Verification','Validation' where not exists (select 1 from tds.activity where activity_name= 'PAN Verification');
insert into tds.activity(activity_name,activity_group) select 'LDC Verification','Validation' where not exists (select 1 from tds.activity where activity_name= 'LDC Verification');
insert into tds.activity(activity_name,activity_group) select 'PDF Transaction','Transaction' where not exists (select 1 from tds.activity where activity_name= 'PDF Transaction');
insert into tds.activity(activity_name,activity_group) select 'TDS Calculation','Transaction' where not exists (select 1 from tds.activity where activity_name= 'TDS Calculation');
insert into tds.activity(activity_name,activity_group) select 'Monthly-Challan Generated','Challans' where not exists (select 1 from tds.activity where activity_name= 'Monthly-Challan Generated');
insert into tds.activity(activity_name,activity_group) select 'Monthly-Challan Receipt Uploaded','Challans' where not exists (select 1 from tds.activity where activity_name= 'Monthly-Challan Receipt Uploaded');
